import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, ViewChild, TemplateRef } from '@angular/core';
import { Router } from '@angular/router';
import { NgbModal, NgbModalRef } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent {
  @ViewChild('content', { static: false }) modalContent!: TemplateRef<any>; // Add "!" to indicate it will be initialized
  @ViewChild('payment', { static: false }) paymentModal!: TemplateRef<any>;

  cardNumber: string = '';
  expirationDate: string = '';
  cvv: string = '';

  selectedFrom: string;
  selectedTo: string;
  selectedDate: string;
  selectedFlight: string;
  availableSeats = 0;
  passengers: string[] = [];
  currentModal: NgbModalRef | null = null;
  selectedMethod: string = '';
  returnDate: string = '';

  isLoading = false;

  roundTrip: boolean = false;

  onRoundTripChange(event: any) {
    this.roundTrip = event.target.checked;
    console.log('Round trip value:', this.roundTrip);
  }

  updatePassengerName(index: number, event: any) {
    const value: string = event.target.value;
    this.passengers[index] = value;
  }

  error: string = '';

  checkSelectedDate() {
    const today = new Date();
    const selected = new Date(this.selectedDate);
    if (selected < today) {
      this.error = 'Please select a future date.';
      this.selectedDate = '';
    } else {
      this.error = '';
    }
  }

  checkReturnDate() {
    const today = new Date(this.selectedDate);
    const selected = new Date(this.returnDate);
    if (selected <= today) {
      this.error = 'Please select a future date.';
      this.returnDate = '';
    } else {
      this.error = '';
    }
  }

  isSame = false;
  totalPrice = 0;
  flights: any[] = [];
  flightId: string = '';
  bookedSeats: any[] = [];

  constructor(
    private http: HttpClient,
    private modalService: NgbModal,
    private route: Router
  ) {
    this.selectedFrom = '';
    this.selectedTo = '';
    this.selectedDate = '';
    this.selectedFlight = '';
    this.totalPrice = 0;
    this.generateSeatRows();
    const token = localStorage.getItem('adminJwtToken');
    if (token) {
      this.route.navigate(['/admin/dashboard']);
      const ownerToken = localStorage.getItem('ownerToken');
      if (ownerToken) {
        this.route.navigate(['/owner/flights']);
      }
    }
  }

  openModal(flight: any, id: string) {
    const token = localStorage.getItem('jwtToken');
    if (token) {
      this.selectedFlight = flight;
      this.flightId = id;
      this.modalService.open(this.modalContent, { size: 'lg' });
      this.http
        .get<any[]>(`http://localhost:5100/flights/${id}`)
        .subscribe((res: any) => {
          if (res) {
            const data = res.reservedSeats.filter((each: { date: string }) => {
              const eachDate = new Date(each.date);
              const selectedDate = new Date(this.selectedDate);
              return eachDate.getDate() === selectedDate.getDate();
            });
            this.bookedSeats = data.map(
              (item: { seat: any; date: any }) => item.seat
            );
          } else {
            this.bookedSeats = [];
          }
        });
    } else {
      this.route.navigate(['/login']);
    }
  }

  openPaymentModal() {
    this.modalService.dismissAll();
    this.modalService.open(this.paymentModal, { centered: true });
  }

  search(): void {
    this.isLoading = true;
    if (this.selectedFrom === this.selectedTo) {
      this.isSame = true;
    } else {
      this.isSame = false;
    }
    this.http.get<any[]>('http://localhost:5100/flights').subscribe((res) => {
      this.flights = res.filter(
        (flight) =>
          flight.origin === this.selectedFrom &&
          flight.destination === this.selectedTo
      );
      this.isLoading = false;
    });
  }

  selectedSeats: string[] = [];
  rows: any[] = [];

  generateSeatRows() {
    const numRows = 10;
    const seatsPerRow = 10;
    const startingRowCharCode = 65;
    for (let i = 0; i < numRows; i++) {
      const rowNumber = String.fromCharCode(startingRowCharCode + i);
      const rowSeats = [];
      for (let j = 1; j <= seatsPerRow; j++) {
        const seatLabel = `${rowNumber}${j}`;
        rowSeats.push(seatLabel);
      }
      this.rows.push({ rowNumber, seats: rowSeats });
    }
  }

  selectSeat(seatNumber: string, price: number) {
    if (this.selectedSeats.includes(seatNumber)) {
      this.selectedSeats = this.selectedSeats.filter(
        (seat) => seat !== seatNumber
      );
    } else {
      this.selectedSeats.push(seatNumber);
    }
    this.totalPrice = price * this.selectedSeats.length;
  }

  confirmBooking() {
    const userId = localStorage.getItem('userId');
    const bookingDetails = {
      user: userId,
      flight: this.flightId,
      passengers: this.passengers,
      totalPrice: this.totalPrice,
      journeyDate: this.selectedDate,
      returnDate: this.returnDate,
      seatNumbers: this.selectedSeats.map((seat) => ({
        seat,
        date: this.selectedDate,
      })),
      paymentMethod: this.selectedMethod,
      paymentStatus: 'success',
    };
    console.log(bookingDetails);
    const response = confirm('Are you sure you want to confirm the booking?');
    if (response) {
      this.http
        .post('http://localhost:5100/bookings', bookingDetails)
        .subscribe((res) => {
          this.currentModal = this.modalService.open(this.paymentModal, {
            size: 'lg',
          });
          console.log(res);
        });
      if (this.currentModal) {
        this.currentModal.dismiss();
      }
    }
  }

  onPayment() {
    let price = this.totalPrice;
    if (this.returnDate !== '') {
      price = this.totalPrice * 2;
    }
    alert(`Payment Successful of ${price}`);
  }

  isFormValid(): boolean {
    if (this.passengers.length === 0) {
      return false;
    }
    return true;
  }

  isPaymentFormValid(): boolean {
    return !!this.cardNumber && !!this.expirationDate && !!this.cvv;
  }

  isDestinationFormValid(): boolean {
    return !!this.selectedFrom && !!this.selectedTo && !!this.selectedDate;
  }
}
<div class="main-container">
  <div class="home-container" id="home">
    <div class="body-container">
      <div class="container text-light" id="banner">
        <h1>Let's start a joyful trip!</h1>
        <p>Explore all the beautiful destinations.</p>
        <div class="d-flex align-items-center mb-2" style="gap: 10px">
          <input
            type="checkbox"
            class="checkbox"
            (change)="onRoundTripChange($event)"
          />
          <label style="font-size: 20px; margin-top: 10px; font-weight: bold"
            >Round Trip</label
          >
          <p>- if you also want to book for return journey.</p>
        </div>

        <div class="input-container mb-4">
          <div class="input-field-container">
            <label for="departure">Departure</label>
            <select
              name="from"
              id="from"
              class="input-field"
              [(ngModel)]="selectedFrom"
            >
              <option value="" selected disabled>Departure City</option>
              <option value="Chennai">Chennai</option>
              <option value="Bengalore">Bengalore</option>
              <option value="Hyderabad">Hyderabad</option>
              <option value="Mumbai">Mumbai</option>
              <option value="Madhya Pradesh">Madhya Pradesh</option>
              <option value="Delhi">Delhi</option>
              <option value="ooty">Ooty</option>
              <option value="Shimla">Shimla</option>
              <option value="indhor">Indhor</option>
              <!-- Add more options as needed -->
            </select>
          </div>
          <div class="input-field-container">
            <label for="destination">Destination</label>
            <select
              name="to"
              id="to"
              class="input-field"
              [(ngModel)]="selectedTo"
            >
              <option value="" selected disabled>Destination City</option>
              <option value="Chennai">Chennai</option>
              <option value="Bengalore">Bengalore</option>
              <option value="Hyderabad">Hyderabad</option>
              <option value="Mumbai">Mumbai</option>
              <option value="Madhya Pradesh">Madhya Pradesh</option>
              <option value="delhi">Delhi</option>
              <option value="ooty">Ooty</option>
              <option value="Shimla">Shimla</option>
              <option value="indhor">Indhor</option>
              <!-- Add more options as needed -->
            </select>
          </div>
          <div class="input-field-container">
            <label for="journey">Journey Date</label>
            <input
              type="date"
              class="input-field"
              [(ngModel)]="selectedDate"
              (change)="checkSelectedDate()"
            />
          </div>
          <div class="input-field-container" *ngIf="roundTrip">
            <label for="return">Return Date</label>
            <input
              type="date"
              class="input-field"
              [(ngModel)]="returnDate"
              (change)="checkReturnDate()"
            />
          </div>
          <div class="button-container">
            <label for=""></label>
            <button
              class="button"
              (click)="search()"
              [disabled]="!isDestinationFormValid()"
            >
              Search
            </button>
          </div>
        </div>

        <p
          class="error-message text-danger"
          style="font-weight: bold; font-size: 28px"
          *ngIf="error"
        >
          {{ error }}
        </p>
        <p
          *ngIf="isSame"
          class="text-danger"
          style="font-weight: bold; font-size: 28px"
        >
          Departure and Destination shouldn't be same...check once
        </p>
        <div class="text-center" *ngIf="isLoading" style="margin-top: 150px">
          <div class="spinner-border" role="status">
            <span class="sr-only">Loading...</span>
          </div>
        </div>
        <div class="row mt-4" *ngIf="flights.length == 0 && !isLoading">
          <h2>no available flights</h2>
        </div>
        <div class="row mt-4" *ngIf="flights.length !== 0 && !isLoading">
          <h1>Available Flights</h1>
          <div class="col-md-12" *ngFor="let flight of flights">
            <div class="card mb-4 shadow">
              <div
                class="card-body d-flex align-items-center justify-content-between"
                style="width: 100%; overflow: auto; gap: 10px"
              >
                <div>
                  <h5 class="card-title">{{ flight.airline }}</h5>
                  <p class="card-text">
                    Flight Number: {{ flight.flightNumber }}
                  </p>
                </div>
                <div>
                  <p class="card-text">Origin: {{ flight.origin }}</p>
                  <p class="card-text" style="color: #9c9c9c">
                    Departure Time: {{ flight.departureTime }}
                  </p>
                </div>
                <div>
                  <p class="card-text">Destination: {{ flight.destination }}</p>
                  <p class="card-text" style="color: #9c9c9c">
                    Arrival Time: {{ flight.arrivalTime }}
                  </p>
                </div>
                <div>
                  <p class="card-text">Price: {{ flight.price }}</p>
                  <p class="card-text" style="color: #9c9c9c">
                    Total Seats: {{ flight.seatsAvailable }}
                  </p>
                </div>
                <button class="button" (click)="openModal(flight, flight._id)">
                  BOOK
                </button>
                <ng-template #content let-modal>
                  <div class="modal-header">
                    <h4 class="modal-title">Select Seats and Fill Details</h4>
                    <button
                      type="button"
                      class="close"
                      aria-label="Close"
                      (click)="modal.dismiss()"
                    >
                      <span aria-hidden="true">&times;</span>
                    </button>
                  </div>
                  <div class="modal-body">
                    <!-- Seat selection and details form -->
                    <!-- ... -->
                    <!-- Seat selection grid -->
                    <div class="seat-grid">
                      <div class="row d-flex" *ngFor="let row of rows">
                        <button
                          class="btn seat d-flex"
                          *ngFor="let seat of row.seats"
                          [ngClass]="{
                            selected: selectedSeats.includes(seat),
                            'green-bg': selectedSeats.includes(seat),
                            'bg-secondary': bookedSeats.includes(seat),
                            disabled: bookedSeats.includes(seat)
                          }"
                          (click)="selectSeat(seat, flight.price)"
                        >
                          {{ seat }}
                        </button>
                      </div>
                    </div>
                    <!-- Selected seats display -->
                    <div class="selected-seats">
                      <p>Selected Seats: {{ selectedSeats.join(", ") }}</p>
                      <p>Total Price: {{ totalPrice }} RS</p>
                    </div>
                    <div class="form-container">
                      <div *ngFor="let seat of selectedSeats; let i = index">
                        <div class="card p-2">
                          <h4>{{ seat }}</h4>
                          <input
                            type="text"
                            placeholder="Enter Passenger name"
                            class="p-2"
                            (input)="updatePassengerName(i, $event)"
                          />
                        </div>
                      </div>
                    </div>
                    <label for="payment" class="mt-2 mb-2"
                      >Payment Method</label
                    >
                    <select
                      name="method"
                      id="payment"
                      class="input-field"
                      [(ngModel)]="selectedMethod"
                    >
                      <option value="" selected disabled>
                        Select Payment Method
                      </option>
                      <option value="creditCard">Credit Cart</option>
                      <option value="debitCard">Debit Card</option>
                      <!-- Add more options as needed -->
                    </select>
                  </div>
                  <div class="modal-footer">
                    <button
                      type="button"
                      class="btn btn-secondary"
                      (click)="modal.dismiss()"
                    >
                      Close
                    </button>
                    <button
                      type="button"
                      class="btn btn-primary"
                      (click)="confirmBooking()"
                      [disabled]="
                        selectedSeats.length === 0 ||
                        passengers.length !== selectedSeats.length
                      "
                    >
                      Confirm Booking
                    </button>
                  </div>
                </ng-template>
                <!-- Payment Modal -->
                <ng-template
                  #payment
                  let-modal
                  *ngIf="selectedMethod === 'creditCard'"
                >
                  <div class="modal-dialog">
                    <div class="modal-content">
                      <div class="modal-header">
                        <h5 class="modal-title">Payment Details</h5>
                        <button
                          type="button"
                          class="close"
                          aria-label="Close"
                          (click)="modal.dismiss()"
                        >
                          <span aria-hidden="true">&times;</span>
                        </button>
                      </div>
                      <div class="modal-body">
                        <!-- Payment content goes here -->
                        <p>Please enter your credit card details:</p>
                        <!-- Credit card input fields -->
                        <input
                          type="text"
                          placeholder="Card Number"
                          [(ngModel)]="cardNumber"
                          class="form-control mb-2"
                        />
                        <input
                          type="text"
                          placeholder="Expiration Date"
                          [(ngModel)]="expirationDate"
                          class="form-control mb-2"
                        />
                        <input
                          type="text"
                          placeholder="CVV"
                          [(ngModel)]="cvv"
                          class="form-control mb-2"
                        />
                      </div>
                      <div class="modal-footer">
                        <button
                          type="button"
                          class="btn btn-secondary"
                          (click)="modal.dismiss()"
                        >
                          Close
                        </button>
                        <button
                          type="button"
                          class="btn btn-primary"
                          (click)="onPayment()"
                          routerLink="/"
                          [disabled]="!isPaymentFormValid()"
                        >
                          Pay Now
                        </button>
                      </div>
                    </div>
                  </div>
                </ng-template>

                <ng-template
                  #payment
                  let-modal
                  *ngIf="selectedMethod === 'debitCard'"
                >
                  <div class="modal-dialog">
                    <div class="modal-content">
                      <div class="modal-header">
                        <h5 class="modal-title">Payment Details</h5>
                        <button
                          type="button"
                          class="close"
                          aria-label="Close"
                          (click)="modal.dismiss()"
                        >
                          <span aria-hidden="true">&times;</span>
                        </button>
                      </div>
                      <div class="modal-body">
                        <!-- Payment content goes here -->
                        <p>Please enter your debit card details:</p>
                        <!-- Debit card input fields -->
                        <input
                          type="text"
                          placeholder="Card Number"
                          [(ngModel)]="cardNumber"
                          class="form-control mb-2"
                        />
                        <input
                          type="text"
                          placeholder="Expiration Date"
                          [(ngModel)]="expirationDate"
                          class="form-control mb-2"
                        />
                        <input
                          type="text"
                          placeholder="CVV"
                          [(ngModel)]="cvv"
                          class="form-control mb-2"
                        />
                      </div>
                      <div class="modal-footer">
                        <button
                          type="button"
                          class="btn btn-secondary"
                          (click)="modal.dismiss()"
                        >
                          Close
                        </button>
                        <button
                          type="button"
                          class="btn btn-primary"
                          (click)="onPayment()"
                          routerLink="/"
                          [disabled]="!isPaymentFormValid()"
                        >
                          Pay Now
                        </button>
                      </div>
                    </div>
                  </div>
                </ng-template>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
  <section id="about" class="section-about p-4">
    <div class="container">
      <h2 class="section-title">About Us</h2>
      <p>
        So with flights being one of the biggest upfront costs of a trip, it’s
        no surprise an abundance of flight booking apps have appeared on the
        scene. But with so many options and limited space on our phones, how can
        we make sure we download the best app for booking flights? Well, the
        answer is different for different people, depending on their travel
        priorities.
      </p>
