import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  regForm: FormGroup;

  constructor(private http: HttpClient, private route: Router) {
    this.regForm = new FormGroup({
      email: new FormControl(null, Validators.required),
      password: new FormControl(null, Validators.required),
    })
    const jwtToken = localStorage.getItem('adminJwtToken')
    if (jwtToken) {
      this.route.navigate(['/admin/dashboard'])
    }
    const token = localStorage.getItem("jwtToken")
    if (token) {

      this.route.navigate(['/'])
    }
    const agentToken = localStorage.getItem("ownerToken")
    if (agentToken) {
      this.route.navigate(['/owner/flights'])
    }
    this.ngOnInit()
  }

  ngOnInit(): void {
    const jwtToken = localStorage.getItem('adminJwtToken')
    if (jwtToken) {
      this.route.navigate(['/admin/dashboard'])
    }
    const token = localStorage.getItem("jwtToken")
    if (token) {

      this.route.navigate(['/'])
    }
    const agentToken = localStorage.getItem("ownerToken")
    if (agentToken) {
      this.route.navigate(['/owner/flights'])
    }
  }

  onSubmit(details = { email: String, password: String }): void {
    console.log(details)
    this.http.post('http://localhost:5100/login', details).subscribe(
      (response: any) => {
        console.log(response)
        if (response && response.user._id) {
          localStorage.setItem('userId', response.user._id)
        }
        if (response && response.token) {
          window.alert('User Login Successfully!');
          this.route.navigate(['/']);
          localStorage.setItem('jwtToken', response.token);
        } else if (response && response.ownerToken) {
          window.alert('Agent Login Successfully!');
          this.route.navigate(['/owner/glights']);
          localStorage.setItem('ownerToken', response.ownerToken);
        } else {
          this.route.navigate(['/admin/dashboard']);
          localStorage.setItem('adminJwtToken', response.jwtTtoken);
          window.alert('Admin Login Successfully!');
        }
      },
      (error) => {
        console.error(error);
        window.alert('Login failed! Email or Password is wrong');
      }
    );
  }


}
<body>
  <div class="container">
    <div class="login-container">
      <div class="card">
        <form
          [formGroup]="regForm"
          #r="ngForm"
          (ngSubmit)="onSubmit(r.value)"
          class="form"
        >
          <h2 class="heading text-center">Login</h2>
          <div class="form-group mb-2">
            <label for="email">Email</label>
            <input
              placeholder="Enter email"
              type="text"
              class="form-control"
              id="email"
              formControlName="email"
              required
            />
            <div
              *ngIf="regForm.controls['email'].touched && regForm.controls['email']?.['errors']"
            >
              <div
                *ngIf="regForm.controls['email']?.['errors']?.['required']"
                class="error-message text-danger"
              >
                Email is required
              </div>
            </div>
          </div>
          <div class="form-group mb-2">
            <label for="password">Password</label>
            <input
              placeholder="Enter password"
              type="text"
              class="form-control"
              id="password"
              formControlName="password"
              required
            />
            <div
              *ngIf="regForm.controls['password'].touched && regForm.controls['password']?.['errors']"
            >
              <div
                *ngIf="regForm.controls['password']?.['errors']?.['required']"
                class="error-message text-danger"
              >
                Password is required
              </div>
            </div>
          </div>
          <button
            type="submit"
            class="btn btn-primary mt-4 w-100"
            [disabled]="regForm.invalid"
          >
            Sign In
          </button>
          <div class="mt-2">
            <a routerLink="/register" class="mt-2"
              >Don't have an account? click here</a
            >
          </div>
          <a routerLink="/airline-login" class="mt-4">Airline? click here.</a>
        </form>
      </div>
    </div>
  </div>
</body>
