import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-bookings',
  templateUrl: './bookings.component.html',
  styleUrls: ['./bookings.component.css'],
})
export class BookingsComponent {
  isLoading = false;
  bookings: any[] = [];
  flights: any[] = [];
  today = new Date();

  constructor(private http: HttpClient) {
    const userId = localStorage.getItem('userId');
    this.isLoading = true;
    this.http
      .get<any[]>(`http://localhost:5100/bookings/user/${userId}`)
      .subscribe((res) => {
        this.bookings = res.sort((a, b) => {
          const dateA = new Date(a.journeyDate);
          const dateB = new Date(b.journeyDate);
          return dateB.getTime() - dateA.getTime();
        });
        console.log(res);
        this.isLoading = false;
      });
  }

  isDateBeforeToday(journeyDate: string): boolean {
    const today = new Date();
    const journey = new Date(journeyDate);
    return (
      journey < new Date(today.getFullYear(), today.getMonth(), today.getDate())
    );
  }

  onCancleTicket(id: string) {
    this.http
      .delete(`http://localhost:5100/bookings/${id}`)
      .subscribe((res) => {
        const userId = localStorage.getItem('userId');
        this.isLoading = true;
        this.http
          .get<any[]>(`http://localhost:5100/bookings/user/${userId}`)
          .subscribe((res) => {
            this.bookings = res.sort((a, b) => {
              const dateA = new Date(a.journeyDate);
              const dateB = new Date(b.journeyDate);
              return dateB.getTime() - dateA.getTime();
            });

            this.isLoading = false;
          });
      });
  }
}
<body>
  <ul class="bookings-container container p-4">
    <h1 align="right">My Bookings 📆</h1>
    <div class="text-center" *ngIf="isLoading" style="margin-top: 150px">
      <div class="spinner-border" role="status">
        <span class="sr-only">Loading...</span>
      </div>
    </div>
    <li
      *ngFor="let booking of bookings; let flight; of: flights"
      class="card mb-4 p-4"
    >
      <div class="divc"><strong>Booking ID:</strong> {{ booking._id }}</div>
      <div class="divc"><strong>User:</strong> {{ booking.user }}</div>
      <div><strong>Flight:</strong> {{ booking.flight }}</div>

      <hr />
      <div>
        <strong>Passengers:</strong>
        <ul>
          <li *ngFor="let passenger of booking.passengers">{{ passenger }}</li>
        </ul>
      </div>
      <div><strong>Total Price:</strong> {{ booking.totalPrice }}</div>
      <div><strong>Booking Date:</strong> {{ booking.date | date }}</div>
      <div><strong>Journey Date:</strong> {{ booking.journeyDate | date }}</div>
      <div>
        <strong>Seat Numbers:</strong>
        <ul>
          <li *ngFor="let seat of booking.seatNumbers">{{ seat.seat }}</li>
        </ul>
      </div>
      <div *ngIf="booking.paymentMethod !== ''">
        <strong>Payment Method:</strong> {{ booking.paymentMethod }}
      </div>

      <div
        *ngIf="booking.paymentMethod !== ''"
        class="d-flex justify-content-between align-items-center"
      >
        <strong class="d-flex"
          >Payment Status:
          <p style="font-weight: lighter; margin-left: 10px">
            {{ booking.paymentStatus }}
          </p></strong
        >
        <button
          class="btn btn-danger"
          (click)="onCancleTicket(booking._id)"
          [disabled]="isDateBeforeToday(booking.journeyDate)"
        >
          Cancel Ticket
        </button>
      </div>
    </li>
  </ul>
</body>
